var dialogFirst=true;function dialog(f,d,c,a,g){if(dialogFirst==true){var e=new String;e='<div id="floatBoxBg"></div>';e+='<div id="floatBox" class="floatBox">';e+='<div class="title"><h4></h4><span>关闭</span></div>';e+='<div class="content"></div>';e+="</div>";$("body").append(e);dialogFirst=false;$("#floatBox .title span").click(function(){$("#floatBoxBg").hide();$("#floatBox").hide()})}$("#floatBox .title h4").html(f);contentType=d.substring(0,d.indexOf(":"));d=d.substring(d.indexOf(":")+1,d.length);switch(contentType){case"url":var b=d.split("?");$("#floatBox .content").ajaxStart(function(){$(this).html("loading...")});$.ajax({type:b[0],url:b[1],data:b[2],error:function(){$("#floatBox .content").html("error...")},success:function(h){$("#floatBox .content").html(h)}});break;case"text":$("#floatBox .content").html(d);break;case"id":$("#floatBox .content").html($("#"+d+"").html());break;case"iframe":$("#floatBox .content").html('<iframe src="'+d+'" width="100%" height="'+(parseInt(a)-30)+'px" scrolling="auto" frameborder="0" marginheight="0" marginwidth="0"></iframe>')}$("#floatBoxBg").css({display:"block",height:$(document).height()});$("#floatBox").removeClass();$("#floatBox").attr("class","floatBox "+g);$("#floatBox").css({display:"block",left:(($(document).width())/2-(parseInt(c)/2))+"px",top:($(document).scrollTop()+50)+"px",width:c,height:a})};
